import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [formData, setFormData] = useState({
    pregnancies: 0,
    glucose: 0,
    bloodpressure: 0,
    skinthickness: 0,
    insulin: 0,
    bmi: 0,
    dpf: 0,
    age: 0
  });
  const [result, setResult] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post("http://localhost:8000/predict", formData);
    setResult(res.data.prediction);  // keep raw prediction (0 or 1)
  };

  return (
    <div className="p-4 text-center">
      <h1 className="text-3xl font-bold text-white drop-shadow-lg mb-6">
        Diabetes Prediction
      </h1>

      {/* Input Form */}
      <form onSubmit={handleSubmit} className="grid gap-3 max-w-md mx-auto">
        {Object.keys(formData).map((key) => (
          <input
            key={key}
            name={key}
            type="number"
            placeholder={key}
            value={formData[key]}
            onChange={handleChange}
            className="border p-2 rounded-lg text-center"
          />
        ))}
        <button
          type="submit"
          className="bg-green-600 text-white font-bold py-2 rounded-lg hover:bg-green-700"
        >
          Predict
        </button>
      </form>

      {/* Prediction Result */}
      {result !== null && (
        <div className="mt-8">
          {result === 1 ? (
            <>
              <h2 className="text-2xl font-bold text-red-500 mb-4">
                Oops! You have Diabetes.
              </h2>
              <img src="/diabetes.webp" alt="Diabetes" className="mx-auto" width="300" />
            </>
          ) : (
            <>
              <h2 className="text-2xl font-bold text-green-400 mb-4">
                Hurrah! You DONâ€™T have Diabetes.
              </h2>
              <img src="/no-diabetes.webp" alt="No Diabetes" className="mx-auto" width="300" />
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
